def showfast (journalist)
  puts "Nombre de Handle : #{journalist.length}"
  puts "Handle le plus court : #{journalist.min_by(&:length)}"
  puts "Nombre de Handle contenant 5 caractères : #{journalist.find {|a| a.size==6}.length}"
  puts "Nombre de Handle commençant par une majuscule : #{journalist.grep(/UPPERCASE/).length}"
  puts "Position de @epenser : #{journalist.index("@epenser")}"
end

def classAlph (journalist)
  puts "Classement Alphabètique : "
  puts journalist.sort{|a,b| a.upcase <=> b.upcase}
end

def classSize (journalist)
  puts "Classement par taille : "
  puts journalist.sort_by(&:length)
end

def showsize (journalist)
  puts "Répartition des Handle par taille (@ compris): "
  nbchara = journalist.sort_by(&:length)[-1].size                                             #on trie la liste du plus petit au plus grand puis on ré&cupére le "-1" ce qui correspond au dernier
  nbletter=journalist.min_by(&:length).size                                                   #selectionne la longeur du plus court
  while nbletter!=nbchara+1
    if journalist.select{|a| a.size==nbletter}.length !=0                                     #n'affiche pas si le nombre de lettre = 0
      puts "Nombre de handle contenant #{nbletter} caractères : #{journalist.select{|a| a.size==nbletter}.length}" 
    end
    nbletter = nbletter + 1
  end
end

def deco
  puts " "
  puts "###############################################################################################"
  puts " "
end

def perform (journalist)
  user_choise = 0
  while user_choise !=5
    puts "Quel info veux tu voir ?"
    puts "  1.Pour les infos qui font une ligne"
    puts "  2.Pour le classement par ordre alphabétique"
    puts "  3.Pour le classement pas taille"
    puts "  4.Pour la répartition des Handle par taille"
    puts "  5.quitter"
    print ">"
    user_choise =gets.chomp.to_i
    if user_choise == 1
      deco
      showfast (journalist)
    end
    if user_choise == 2
      deco
      classAlph (journalist)
    end
    if user_choise == 3
      deco
      classSize (journalist)
    end
    if user_choise == 4
      deco
      showsize (journalist)
    end
    deco
  end
end
perform(journalist)
